import { assert } from '@polkadot/util';
import { TypeRegistry } from "./create/index.js";

function registryCheck(registry) {
  // Should be AccountId
  const aa = registry.createType('AccountId');
  assert(aa.isAscii, 'All ok'); // Should be Codec

  const bb = registry.createType('Something');
  assert(bb.toHuman(), 'All ok'); // Should be u32

  const cc = registry.createType('Something');
  assert(cc.divn(123), 'All ok'); // Should be u32

  const dd = registry.createType('Something');
  assert(dd.divn(123), 'All ok');
}

registryCheck(new TypeRegistry());